import React, {Component} from 'react';

import Item from '../Item';


class TitleList extends Component {

    componentDidMount() {
        if (this.props.url !== '') {
            this.loadContent();
            this.setState({
                data : ['tite'],
                mounted: true
            });
        }
    }

    loadContent() {
        var requestUrl = 'https://api.themoviedb.org/3/' + this.props.url + '&api_key=' + '428fe45ad7b2d15d210185ac3f4a6bf2';
        fetch(requestUrl)
            .then((response) => {
                return response.json();
            })
            .then((data) => {
                this.setState({data: data});
                console.log(this.state.data)
            }).catch((err) => {
            console.log('there has been an error');
        })
    }

    render() {
        var titles = "";
        if(this.state.data.results) {
            titles = this.state.data.results.map(function(title, i) {
                if(i < 5) {
                    var name = "";
                    var backDrop =
                            "https://image.tmdb.org/t/p/original" + title.backDrop_path;
                    if (!title.name) {
                        name = title.original_title;
                    } else {
                        name = title.name
                    }
                    return (
                        <Item
                            key = {title.id}
                            title = {name}
                            score = {title.vote_average}
                            overview = {title.overview}
                            backDrop = {backDrop}
                        />
                    );
                } else {
                    return <div key={title.id} />;
                }
            });
        }
        return (
            <div
                ref="titlecategory"
                className="TitleList"
                data_loaded={this.state.mounted}
            >
                <div className="title">
                    <h1>{this.props.title}</h1>
                    <div className="titles-wrapper">{titles}</div>
                </div>
            </div>
        )
    }
}

export default TitleList;
