        if (this.props.url !== '') {
            this.loadContent();
            this.setState({mounted: true});
        }
    }

    loadContent() {
        var requestUrl = 'https://api.themoviedb.org/3/' + this.props.url + '&api_key=' + '428fe45ad7b2d15d210185ac3f4a6bf2';
        fetch(requestUrl)
            .then((response) => {
