import React, {Component} from 'react';
import './App.css';
import Logo from './components/Logo';
import Navigation from './components/Navigation';
import UserProfile from './components/UserProfile';
import Hero from './components/Hero';
import TitleList from './components/TitleList';
import Item from './components/Item';


class App extends Component {

    apiKey = '428fe45ad7b2d15d210185ac3f4a6bf2';

    constructor(props) {
        super(props);

        this.state = {
            searchTerm: "",
            searchUrl:  ""
        };

        this.handleKeyUp  = this.handleKeyUp.bind();
        this.handleChange = this.handleChange.bind();
    }

    handleKeyUp = (e) => {
        if (e.key === 'Enter' && this.state.searchTerm !== '') {
            var searchUrl = "search/multi?query=" + this.state.searchTerm + "&api_key=" + this.apiKey;
            console.log(searchUrl);
            this.setState({searchUrl: searchUrl});
        }
    };

    handleChange = (e) => {
        this.setState({searchTerm: e.target.value});
        console.log(this.state.searchTerm);
    };

    render() {
        return (
            <div>
                <div className={"Header"}>
                    <Logo/>
                    <Navigation/>
                    <div id="search" className={"Search"}>
                        <input onKeyUp={this.handleKeyUp} onChange={this.handleChange} type="search"
                               placeholder="Search for a title..." value={this.state.searchTerm}/>
                        <UserProfile/>
                    </div>
                    <Hero/>
                    <TitleList title="Search Results" url={this.state.searchurl}/>
                    <TitleList title="Top TV picks for Rauf" url='discover/tv?sort_by=popularity.desc&page=1'/>
                    <TitleList title="Trending now" url='discover/movie?sort_by=popularity.desc&page=1'/>
                    <TitleList title="Most watched in Horror" url='genre/27/movies?sort_by=popularity.desc&page=1'/>
                    <TitleList title="Sci-Fi greats" url='genre/878/movies?sort_by=popularity.desc&page=1'/>
                    <TitleList title="Comedy magic" url='genre/35/movies?sort_by=popularity.desc&page=1'/>
                </div>
            </div>
        );
    }
}

export default App;
